import {
  CREATE_ORDER_FAIL,
  CREATE_ORDER_LOAD,
  CREATE_ORDER_SUCCESS,
} from "../actions/types";

const initialState = {
  error: null,
  loading: false,
  orders: [],
  order: null,
  success: false,
};

const orderReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case CREATE_ORDER_SUCCESS:
      return {
        ...state,
        loading: false,
        error: null,
        success: true,
        order: payload,
      };
    case CREATE_ORDER_FAIL:
      return {
        ...state,
        loading: false,
        error: payload,
        success: false,
      };
    case CREATE_ORDER_LOAD:
      return {
        ...state,
        loading: true,
        success: false,
      };
    default:
      return state;
  }
};

export default orderReducer;
