import axios from "axios";
import {
  LOGIN_USER_FAIL,
  LOGIN_USER_LOAD,
  LOGIN_USER_SUCCESS,
  LOGOUT_USER,
  REGISTER_USER_SUCCESS,
  UPDATE_USER_FAIL,
} from "./types";

export const loginUser = (email, password) => {
  return async (dispatch) => {
    dispatch({
      type: LOGIN_USER_LOAD,
    });

    try {
      const userModel = { email, password };
      const { data } = await axios.post(`/api/users/login`, userModel, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      dispatch({
        type: LOGIN_USER_SUCCESS,
        payload: data,
      });

      localStorage.setItem("user", JSON.stringify(data));
    } catch (error) {
      dispatch({
        type: LOGIN_USER_FAIL,
        payload: error.response ? error.response.data.message : error.message,
      });
    }
  };
};

export const logout = () => {
  return (dispatch) => {
    localStorage.removeItem("user");
    dispatch({
      type: LOGOUT_USER,
    });
  };
};

export const registerUser = (email, password, name, isAdmin = false) => {
  return async (dispatch) => {
    dispatch({
      type: LOGIN_USER_LOAD,
    });
    try {
      const user = await axios.post(
        "/api/users/register",
        {
          email,
          password,
          isAdmin,
          name,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      dispatch({
        type: REGISTER_USER_SUCCESS,
        payload: user.data,
      });

      localStorage.setItem("user", user.data);
    } catch (error) {
      dispatch({
        type: LOGIN_USER_FAIL,
        payload: error.response ? error.response.data.message : error.message,
      });
    }
  };
};

export const updateUserProfile = (email, password, name, isAdmin = false) => {
  return async (dispatch) => {
    dispatch({
      type: UPDATE_USER_LOAD,
    });
    try {
      const user = await axios.put(
        "/api/users/profile",
        {
          email,
          password,
          isAdmin,
          name,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      dispatch({
        type: UPDATE_USER_SUCCESS,
        payload: user.data,
      });

      localStorage.setItem("user", user.data);
    } catch (error) {
      dispatch({
        type: UPDATE_USER_FAIL,
        payload: error.response ? error.response.data.message : error.message,
      });
    }
  };
};
