import React, { useEffect } from "react";
import { Fragment } from "react";
import { Button, Col, Row, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { LinkContainer } from "react-router-bootstrap";
import { getAllOrders } from "../actions/order";
import { deleteProduct, getProducts } from "../actions/product";
import Loader from "../components/Loader";
import Message from "../components/Message";

const OrderListScreen = ({ history }) => {
  const dispatch = useDispatch();

  const order = useSelector((state) => state.order);
  const { loading, error, orders } = order;

  const auth = useSelector((state) => state.auth);
  const { user: userInfo } = auth;

  useEffect(() => {
    if (userInfo && userInfo.isAdmin) {
      dispatch(getAllOrders());
    } else {
      history.push("/");
    }
  }, [history, userInfo, dispatch]);

  const deleteHandler = (id) => {
    if (window.confirm("Are you sure")) {
      dispatch(deleteProduct(id));
    }
  };

  const createProductHandler = (e) => {
    e.preventDefault();
    history.push("/admin/products/add");
    //   dispatch(createPro)
  };

  return (
    <Fragment>
      <h1>Orders</h1>
      {/* <Row>
        <Col>
          <h1>Products</h1>
        </Col>
        <Col className="text-right">
          <Button className="my-3" onClick={createProductHandler}>
            <i className="fas fa-plus">
              {" "}Create Product
            </i>
          </Button>
        </Col>
      </Row> */}
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Table striped hover responsive bordered className="table-sm">
          <thead>
            <tr>
              <td>ID</td>
              <td>User</td>
              <td>Total</td>
              <td>Date</td>
              <td>Paid</td>
              <td>Delivered</td>
              <td></td>
            </tr>
          </thead>
          {orders.map((i) => (
            <tr key={i._id}>
              <td>{i._id}</td>
              <td>{i.user.name}</td>
              <td>${i.totalPrice.toFixed(2)}</td>
              <td>{i.createdAt.subs}</td>
              <td>
                {" "}
                {i.isPaid ? (
                  <i className="fas fa-check" style={{ color: "green" }}></i>
                ) : (
                  <i className="fas fa-times" style={{ color: "red" }}></i>
                )}
              </td>
              <td>
                {" "}
                {i.isDelivered ? (
                  <i className="fas fa-check" style={{ color: "green" }}></i>
                ) : (
                  <i className="fas fa-times" style={{ color: "red" }}></i>
                )}
              </td>
              <td>
                <LinkContainer to={`/admin/orders/${i._id}`}>
                  <Button variant="light" className="btn-sm">
                    <i className="fas fa-edit"></i>
                  </Button>
                </LinkContainer>
                <Button
                  variant="danger"
                  className="btn-sm"
                  onClick={() => deleteHandler(i._id)}
                >
                  <i className="fas fa-trash"></i>
                </Button>
              </td>
            </tr>
          ))}
        </Table>
      )}
    </Fragment>
  );
};

export default OrderListScreen;
