import {
  CREATE_ORDER_FAIL,
  CREATE_ORDER_LOAD,
  CREATE_ORDER_SUCCESS,
  GET_ORDERS_FAIL,
  GET_ORDERS_SUCCESS,
  GET_ORDER_FAIL,
  GET_ORDER_SUCCESS,
  PAY_ORDERS_FAIL,
  PAY_ORDER_SUCCESS,
} from "../actions/types";

const initialState = {
  error: null,
  loading: false,
  orders: [],
  successPay: false,
  order: null,
  success: false,
};

const orderReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case CREATE_ORDER_SUCCESS:
      return {
        ...state,
        loading: false,
        error: null,
        success: true,
        order: payload,
      };
    case CREATE_ORDER_FAIL:
      return {
        ...state,
        loading: false,
        error: payload,
        success: false,
        successPay: false,
      };
    case CREATE_ORDER_LOAD:
      return {
        ...state,
        loading: true,
        success: false,
        successPay: false,
      };
    case GET_ORDER_SUCCESS:
      return {
        ...state,
        error: null,
        order: payload,
        loading: false,
      };
    case PAY_ORDER_SUCCESS:
      return {
        ...state,
        error: null,
        order: null,
        loading: false,
        successPay: true,
      };
    case GET_ORDERS_FAIL:
      return {
        ...state,
        error: payload,
        loading: false,
        successPay: false,
      };

    case PAY_ORDERS_FAIL:
      return {
        ...state,
        error: payload,
        order: null,
        successPay: false,
        loading: false,
      };
    case GET_ORDERS_SUCCESS:
      return {
        ...state,
        orders: payload,
        error: null,
        loading: false,
      };
    case GET_ORDER_FAIL:
      return {
        ...state,
        error: payload,
        loading: false,
        successPay: false,
      };
    default:
      return state;
  }
};

export default orderReducer;
