const {
  LOGIN_USER_LOAD,
  LOGIN_USER_SUCCESS,
  LOGIN_USER_FAIL,
  LOGOUT_USER
} = require("../actions/types");

const initialState = {
  user: null,
  loading: false,
  erro: null,
};

const authReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case LOGIN_USER_LOAD:
      return {
        ...state,
        loading: true,
      };
    case LOGIN_USER_SUCCESS:
      return {
        ...state,
        loading: false,
        user: payload,
      };
    case LOGIN_USER_FAIL:
      return {
        ...state,
        error: payload,
        loading: false,
      };
    case LOGOUT_USER: 
      return {
          ...state,
          user: null
      }
    default:
      return state;
  }
};

export default authReducer;
