import {
  GET_PRODUCT,
  GET_PRODUCTS,
  GET_PRODUCTS_START,
  GET_PRODUCTS_FAIL,
} from "../actions/types";

const initialState = {
  products: [],
  product: null,
  loading: false,
};

const productReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case GET_PRODUCTS_START:
      return {
        ...state,
        loading: true,
      };
    case GET_PRODUCTS:
      return {
        ...state,
        loading: false,
        products: payload,
      };
    case GET_PRODUCTS_FAIL:
      return {
        ...state,
        loading: false,
        products: [],
        product: null,
      };
    case GET_PRODUCT:
      return {
        ...state,
        loading: false,
        product: payload,
      };
    default:
      return state;
  }
};

export default productReducer;
