const { CART_ITEM_ADD } = require("../actions/types");

const initialState = {
  cartItems: [],
  error: null,
  loading: false,
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case CART_ITEM_ADD:
      const item = payload;
      const itemExist = state.cartItems.filter((i) => i._id === item._id);
  }
};
