import {
  GET_ORDER_FAIL,
  GET_USERS_LOAD,
  GET_USERS_SUCCESS,
} from "../actions/types";

const initialState = {
  users: [],
  error: null,
  loading: false,
};

const userReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case GET_USERS_SUCCESS:
      return {
        ...state,
        users: payload,
        loading: false,
      };
    case GET_USERS_LOAD:
      return {
        ...state,
        loading: true,
      };

    case GET_ORDER_FAIL:
      return {
        ...state,
        loading: false,
        error: payload,
      };
    default:
      return state;
  }
};

export default userReducer;
