const { CART_ITEM_ADD } = require("../actions/types");

const initialState = {
  cartItems: [],
  error: null,
  loading: false,
};

const reducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case CART_ITEM_ADD:
      const item = payload;
      const itemExist = state.cartItems.find((i) => i.product === item.product);
      if (itemExist) {
          return {
              ...state,
              cartItems: state.cartItems
          }
      } else {
        return {
          ...state,
          cartItems: [...state.cartItems, item],
        };
      }
  }
};
